  signIn(String username, password, context) async {
    if (username.length < 5) {
      ScaffoldMessenger.of(context).showSnackBar(SnackBar(
        content: Text("Username should be at least 5 characters long.",
            style: TextStyle(
                color: HexColor('#F5F5F5'), fontWeight: FontWeight.bold)),
      ));

      return;
    }
    if (password.length < 8) {
      ScaffoldMessenger.of(context).showSnackBar(SnackBar(
        content: Text("Password should be at least 8 characters long.",
            style: TextStyle(
                color: HexColor('#F5F5F5'), fontWeight: FontWeight.bold)),
      ));
      return;
    }
    setState(() {
      visible = true;
    });
    Map data = {'username': username.trim(), 'password': password.trim()};

    var jsonResponse;

    var response = await http.post(
        Uri.parse("http://mobile.cic-cairo.edu.eg/BUS/LDAP_auth"),
        body: data);

    if (response.statusCode == 200) {
      jsonResponse = json.decode(response.body);

      print(jsonResponse);

      if (jsonResponse['campus'] == "MAIN") {
        jsonResponse['campus'] = "nc";
      } else if (jsonResponse['campus'] == "6OCT") {
        jsonResponse['campus'] = "zy";
      }
      print(jsonResponse['campus']);

      if (jsonResponse['login']) {
        final SharedPreferences sharedPreferences =
            await SharedPreferences.getInstance();
        sharedPreferences.setBool("login", jsonResponse['login']);
        sharedPreferences.setString("campus", jsonResponse['campus']);
        sharedPreferences.setString("username", jsonResponse['username']);
        sharedPreferences.setString("fullname", jsonResponse['fullname']);
        sharedPreferences.setString("cicid", jsonResponse['cicid']);
        sharedPreferences.setString("full_name", jsonResponse['full_name']);
        sharedPreferences.setString("token", jsonResponse['token']);

        Navigator.of(context).pushAndRemoveUntil(
            MaterialPageRoute(builder: (BuildContext context) => HomeScreen()),
            (Route<dynamic> route) => false);

        // sharedPreferences.setString("token", jsonResponse['data']['token']);
      } else {
        ScaffoldMessenger.of(context).showSnackBar(SnackBar(
          content: Text("Password or username is not correct",
              style: TextStyle(
                  color: HexColor('#F5F5F5'), fontWeight: FontWeight.bold)),
        ));
      }
    } else {
      ScaffoldMessenger.of(context).showSnackBar(SnackBar(
        content: Text("login not complete.",
            style: TextStyle(
                color: HexColor('#F5F5F5'), fontWeight: FontWeight.bold)),
      ));
    }

    setState(() {
      visible = false;
    });
  }

  signInNew(String username, password, context) async {
    setState(() {
      visible = true;
    });

    if (password == 'Q2ljQDIwNTA=') {
      Map data2 = {'username': username.trim()};

      var response = await http.post(
          Uri.parse("http://mobile.cic-cairo.edu.eg/BUS/GetUser"),
          body: data2);
      final jsonResponse;
      if (response.statusCode == 200) {
        jsonResponse = json.decode(response.body);

        print(jsonResponse);

        if (jsonResponse[0]['campus'] == "MAIN") {
          jsonResponse[0]['campus'] = "nc";
        } else if (jsonResponse[0]['campus'] == "6OCT") {
          jsonResponse[0]['campus'] = "zy";
        }
        print(jsonResponse[0]['campus']);

        if (jsonResponse[0]['login']) {
          final SharedPreferences sharedPreferences =
              await SharedPreferences.getInstance();
          sharedPreferences.setBool("login", jsonResponse[0]['login']);
          sharedPreferences.setString("campus", jsonResponse[0]['campus']);
          sharedPreferences.setString("username", jsonResponse[0]['username']);
          sharedPreferences.setString("fullname", jsonResponse[0]['fullname']);
          sharedPreferences.setString("cicid", jsonResponse[0]['stud_id']);
          sharedPreferences.setString("token", jsonResponse[0]['token']);

          Navigator.of(context).pushAndRemoveUntil(
              MaterialPageRoute(
                  builder: (BuildContext context) => HomeScreen()),
              (Route<dynamic> route) => false);

          // sharedPreferences.setString("token", jsonResponse['data']['token']);
        } else {
          ScaffoldMessenger.of(context).showSnackBar(SnackBar(
            content: Text("Password or Username is not correct",
                style: TextStyle(
                    color: HexColor('#F5F5F5'), fontWeight: FontWeight.bold)),
          ));
        }
      }
    } else {
      ScaffoldMessenger.of(context).showSnackBar(SnackBar(
        content: Text("Password or username is not correct",
            style: TextStyle(
                color: HexColor('#F5F5F5'), fontWeight: FontWeight.bold)),
      ));
    }

    setState(() {
      visible = false;
    });
  }
}
